import { useState, useRef, useEffect } from "react";
import { Github, Mail } from "lucide-react";
import { useNavigate } from "react-router-dom";
import { auth, database } from "./firebase";
import { createUserWithEmailAndPassword, signInWithEmailAndPassword } from "firebase/auth";
import { ref, get } from "firebase/database";
import "./App.css";
import darkblue from "./assets/darkblue.mp4";

export default function App() {
  const [isLogin, setIsLogin] = useState(true);
  const [userId, setUserId] = useState("");
  const [password, setPassword] = useState("");
  const [confirmPassword, setConfirmPassword] = useState("");
  const [error, setError] = useState("");

  const videoRef = useRef(null);
  const containerRef = useRef(null);
  const navigate = useNavigate();

  useEffect(() => {
    if (videoRef.current) {
      videoRef.current.play().catch((error) => {
        console.error("Error attempting to play video: ", error);
      });
    }
  }, []);

  const handleScroll = () => {
    if (containerRef.current) {
      containerRef.current.scrollIntoView({ behavior: "smooth" });
    }
  };

  const checkUserRole = async (userId) => {
    const dbRef = ref(database);
    const adminSnapshot = await get(ref(dbRef, `Admin`));
    const facultySnapshot = await get(ref(dbRef, `Faculty`));
    const studentSnapshot = await get(ref(dbRef, `Student`));

    if (adminSnapshot.exists()) {
      const adminData = adminSnapshot.val();
      if (adminData.some(admin => admin && admin.userId === parseInt(userId))) {
        return "admin";
      }
    }

    if (facultySnapshot.exists()) {
      const facultyData = facultySnapshot.val();
      if (facultyData.some(faculty => faculty && faculty.userId === parseInt(userId))) {
        return "faculty";
      }
    }

    if (studentSnapshot.exists()) {
      const studentData = studentSnapshot.val();
      if (studentData.some(student => student && student.userId === parseInt(userId))) {
        return "student";
      }
    }

    return null;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError("");

    if (!isLogin && password !== confirmPassword) {
      setError("Passwords do not match");
      return;
    }

    try {
      const userRole = await checkUserRole(userId);

      if (!userRole) {
        setError("User ID not found in the database");
        return;
      }

      const email = `${userId}@example.com`; // Create a dummy email using userId

      if (isLogin) {
        await signInWithEmailAndPassword(auth, email, password);
      } else {
        await createUserWithEmailAndPassword(auth, email, password);
      }

      // Route based on user role
      if (userRole === "admin") {
        navigate("/admin/AdminDashboard");
      } else {
        navigate("/user/chat");
      }
    } catch (err) {
      setError(err.message);
      console.error("Authentication error:", err);
    }
  };

  return (
    <div className="overlay">
      <div id="gradient-overlay"></div>
      <video ref={videoRef} id="background-video" loop autoPlay muted>
        <source src={darkblue} type="video/mp4" />
        Your browser does not support the video tag.
      </video>
      <header className="header">
        <nav className="navbar">
          <ul className="nav-links">
            <li><button className="nav-button">Home</button></li>
            <li><button className="nav-button">About</button></li>
            <li><button className="nav-button">Blog</button></li>
            <li><button className="nav-button">Contact Us</button></li>
          </ul>
        </nav>
      </header>
      <div ref={containerRef} className="container">
        <div className="card">
          <div className="card-header">
            <h2>Welcome</h2>
            <p>Sign in to your account or create a new one</p>
          </div>
          <div className="auth-options">
            <button className="auth-button">
              <Mail className="icon" />
              Google
            </button>
            <button className="auth-button">
              <Github className="icon" />
              Github
            </button>
          </div>
          <div className="divider">
            <span>OR</span>
          </div>
          <div className="toggle-container">
            <label>Login</label>
            <input
              type="checkbox"
              checked={!isLogin}
              onChange={() => setIsLogin(!isLogin)}
              className="toggle-switch"
            />
            <label>Sign Up</label>
          </div>
          <form onSubmit={handleSubmit}>
            <div className="form-group">
              <label>User ID</label>
              <input
                type="text"
                placeholder="Enter your User ID"
                className="input-field"
                value={userId}
                onChange={(e) => setUserId(e.target.value)}
                required
              />
            </div>
            <div className="form-group">
              <label>Password</label>
              <input
                type="password"
                className="input-field"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                required
              />
            </div>
            {!isLogin && (
              <div className="form-group">
                <label>Confirm Password</label>
                <input
                  type="password"
                  className="input-field"
                  value={confirmPassword}
                  onChange={(e) => setConfirmPassword(e.target.value)}
                  required
                />
              </div>
            )}
            <button className="submit-button" type="submit">
              {isLogin ? "Login" : "Sign Up"}
            </button>
            {error && <p className="error-message">{error}</p>}
          </form>
        </div>
      </div>
    </div>
  );
}
