import React, { useState, useRef } from "react";
import axios from "axios";
import {
  Upload,
  Bell,
  BarChart2,
  MessageSquare,
  MoreVertical,
  FileText,
  FileImage,
  FileSpreadsheet,
  File,
  FileType,
  Image,
  LogOut,
  Paperclip,
  X
} from "lucide-react";
import "./AdminDashboard.css";

export default function AdminDashboard() {
  const [activeTab, setActiveTab] = useState("Upload");
  const [announcement, setAnnouncement] = useState("");
  const [files, setFiles] = useState([]);
  const [announcementFiles, setAnnouncementFiles] = useState([]);
  const [uploading, setUploading] = useState(false);
  const [uploadError, setUploadError] = useState(null);
  const [selectedRole, setSelectedRole] = useState("Students");
  const fileInputRef = useRef(null);
  const announcementFileInputRef = useRef(null);

  const tabs = [
    { name: "Upload", icon: Upload },
    { name: "Announcements", icon: Bell },
    { name: "Stats", icon: BarChart2 },
    { name: "Chatbot", icon: MessageSquare },
  ];

  const handleLogout = async () => {
    try {
      await auth.signOut();
      console.log('User logged out successfully');
      navigate('/login');
    } catch (error) {
      console.error('Error logging out:', error);
    }
  };

  const handleAnnouncementSubmit = async (e) => {
    e.preventDefault();
    setUploading(true);
    setUploadError(null);

    try {
      const announcementBlob = new Blob([announcement], { type: 'text/plain' });
      const textFile = new File([announcementBlob], 'announcement.txt', { type: 'text/plain' });

      const formData = new FormData();
      formData.append('files', textFile);
      announcementFiles.forEach(file => {
        formData.append('files', file);
      });
      formData.append('role', selectedRole.toLowerCase());

      const response = await axios.post(
        "http://localhost:8000/upload/",
        formData,
        {
          headers: {
            "Content-Type": "multipart/form-data",
          },
        }
      );

      console.log("Announcement and files uploaded:", response.data);
      
      const newFiles = [...announcementFiles, textFile].map(file => ({
        name: file.name,
        size: (file.size / 1024 / 1024).toFixed(2) + " MB",
        uploaded: new Date().toLocaleDateString(),
        icon: getFileIcon(file.name),
        role: selectedRole,
      }));

      setFiles(prevFiles => [...newFiles, ...prevFiles]);
      setAnnouncement("");
      setAnnouncementFiles([]);

    } catch (error) {
      console.error("Error uploading announcement:", error);
      setUploadError("Failed to upload announcement. Please try again.");
    } finally {
      setUploading(false);
    }
  };

  const handleFileUpload = async (e) => {
    const selectedFiles = Array.from(e.target.files);
    setUploading(true);
    setUploadError(null);
    const formData = new FormData();
    selectedFiles.forEach((file) => {
      formData.append("files", file);
    });
    formData.append("role", selectedRole.toLowerCase());

    try {
      const response = await axios.post(
        "http://localhost:8000/upload/",
        formData,
        {
          headers: {
            "Content-Type": "multipart/form-data",
          },
        }
      );
      console.log(response.data);
      const newFiles = selectedFiles.map((file) => ({
        name: file.name,
        size: (file.size / 1024 / 1024).toFixed(2) + " MB",
        uploaded: new Date().toLocaleDateString(),
        icon: getFileIcon(file.name),
        role: selectedRole,
      }));
      setFiles((prevFiles) => [...newFiles, ...prevFiles]);
    } catch (error) {
      console.error("Error uploading file:", error);
      setUploadError("Failed to upload files. Please try again.");
    } finally {
      setUploading(false);
    }
  };

  const handleAnnouncementFileUpload = (e) => {
    const selectedFiles = Array.from(e.target.files);
    setAnnouncementFiles(prevFiles => [...prevFiles, ...selectedFiles]);
  };

  const removeAnnouncementFile = (index) => {
    setAnnouncementFiles(prevFiles => prevFiles.filter((_, i) => i !== index));
  };

  const triggerFileUpload = () => {
    fileInputRef.current.click();
  };

  const triggerAnnouncementFileUpload = () => {
    announcementFileInputRef.current.click();
  };

  const getFileIcon = (fileName) => {
    const ext = fileName.split('.').pop().toLowerCase();
    switch (ext) {
      case 'pdf':
        return FileText;
      case 'csv':
      case 'xlsx':
        return FileSpreadsheet;
      case 'txt':
        return FileText;
      case 'docx':
        return FileType;
      case 'jpeg':
      case 'jpg':
      case 'png':
        return Image;
      default:
        return File;
    }
  };

  return (
    <div className="app-container">
      <nav>
        <div className="nav-content">
          <div className="nav-top">
            <h1>Nexus Control</h1>
            <ul>
              {tabs.map((tab) => (
                <li key={tab.name}>
                  <button
                    className={activeTab === tab.name ? "active" : ""}
                    onClick={() => setActiveTab(tab.name)}
                  >
                    <tab.icon />
                    {tab.name}
                  </button>
                </li>
              ))}
            </ul>
          </div>
          <button className="logout-button" onClick={handleLogout}>
            <LogOut />
            Logout
          </button>
        </div>
      </nav>
      <main className="main-layout">
        {(activeTab === "Upload" || activeTab === "Announcements") && (
          <div className="dropdown-container">
            <select
              id="userType"
              value={selectedRole}
              onChange={(e) => setSelectedRole(e.target.value)}
              className="user-type-dropdown"
            >
              <option value="Students">Students</option>
              <option value="Teachers">Teachers</option>
              <option value="HOD/Dean">HOD/Dean</option>
            </select>
          </div>
        )}
        
        <div className="content-area">
          {activeTab === "Upload" && (
            <div className="upload-section">
              <h2>Data Upload Interface</h2>
              <div className="card">
                <div className="upload-area">
                  <p>Initiate data transfer or select files for upload.</p>
                  <p className="file-types">Supported file types: PDF, CSV, TXT, DOCX, XLSX, JPEG, JPG, PNG</p>
                  <button onClick={triggerFileUpload} disabled={uploading}>
                    {uploading ? "Uploading..." : "Upload Files"}
                  </button>
                  <input
                    type="file"
                    ref={fileInputRef}
                    className="hidden"
                    onChange={handleFileUpload}
                    multiple
                    accept=".pdf,.csv,.txt,.docx,.xlsx,.jpeg,.jpg,.png"
                  />
                </div>
                {uploadError && (
                  <p className="error-message">{uploadError}</p>
                )}
              </div>
              <div className="card">
                <table>
                  <thead>
                    <tr>
                      <th>File</th>
                      <th>Size</th>
                      <th>Uploaded</th>
                      <th>Role</th>
                      <th>Actions</th>
                    </tr>
                  </thead>
                  <tbody>
                    {files.map((file, index) => (
                      <tr key={index}>
                        <td>
                          <div className="file-info">
                            <div className="file-icon">
                              {file.icon && <file.icon />}
                            </div>
                            <span>{file.name}</span>
                          </div>
                        </td>
                        <td>{file.size}</td>
                        <td>{file.uploaded}</td>
                        <td>{file.role}</td>
                        <td>
                          <button className="action-button">
                            <MoreVertical />
                          </button>
                        </td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              </div>
            </div>
          )}
          
          {activeTab === "Announcements" && (
            <div className="announcements-section">
              <h2>Broadcast Announcements</h2>
              <div className="card">
                <form onSubmit={handleAnnouncementSubmit}>
                  <textarea
                    value={announcement}
                    onChange={(e) => setAnnouncement(e.target.value)}
                    placeholder="Type your announcement here..."
                    className="announcement-textarea"
                  />
                  <div className="file-upload-section">
                    <button 
                      type="button" 
                      onClick={triggerAnnouncementFileUpload}
                      className="attach-button"
                    >
                      <Paperclip />
                      Attach Files
                    </button>
                    <input
                      type="file"
                      ref={announcementFileInputRef}
                      className="hidden"
                      onChange={handleAnnouncementFileUpload}
                      multiple
                      accept=".pdf,.csv,.txt,.docx,.xlsx,.jpeg,.jpg,.png"
                    />
                    <div className="attached-files">
                      {announcementFiles.map((file, index) => (
                        <div key={index} className="attached-file">
                          <div className="file-info">
                            <div className="file-icon">
                              {React.createElement(getFileIcon(file.name))}
                            </div>
                            <span>{file.name}</span>
                          </div>
                          <button 
                            type="button" 
                            className="remove-file-button"
                            onClick={() => removeAnnouncementFile(index)}
                          >
                            <X size={16} />
                          </button>
                        </div>
                      ))}
                    </div>
                  </div>
                  <button 
                    type="submit" 
                    className="submit-announcement-button"
                    disabled={uploading}
                  >
                    {uploading ? "Submitting..." : "Submit Announcement"}
                  </button>
                  {uploadError && (
                    <p className="error-message">{uploadError}</p>
                  )}
                </form>
              </div>
            </div>
          )}
          
          {activeTab === "Stats" && (
            <div className="stats-section">
              <h2>Statistics</h2>
              <div className="card">
                <p>Statistics content goes here.</p>
              </div>
            </div>
          )}
          
          {activeTab === "Chatbot" && (
            <div className="chatbot-section">
              <h2>Chatbot Interface</h2>
              <div className="card">
                <p>Chatbot interface goes here.</p>
              </div>
            </div>
          )}
        </div>
      </main>
    </div>
  );
}
