import React, { createContext, useContext, useState, useEffect } from 'react';
import { auth } from './firebase';
import { onAuthStateChanged, signOut } from 'firebase/auth';
import { doc, getDoc } from 'firebase/firestore';
import { db } from './firebase';

const AuthContext = createContext(null);

export const AuthProvider = ({ children }) => {
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);

  // Function to handle admin login
  const loginAdmin = (email) => {
    setUser({
      uid: 'admin',
      email: email,
      role: 'admin',
      name: 'Administrator'
    });
    localStorage.setItem('adminSession', 'true');
    setLoading(false);
  };

  const logout = async () => {
    try {
      setLoading(true);
      // Clear admin session if exists
      localStorage.removeItem('adminSession');
      
      // Sign out from Firebase if not admin
      if (auth.currentUser) {
        await signOut(auth);
      }
      
      // Clear user state
      setUser(null);
    } catch (error) {
      console.error('Logout error:', error);
      throw error; // Propagate error to be handled by components
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    // Check for admin session first
    const adminSession = localStorage.getItem('adminSession');
    if (adminSession) {
      loginAdmin('admin@test.com');
      return;
    }

    // Regular Firebase auth listener
    const unsubscribe = onAuthStateChanged(auth, async (firebaseUser) => {
      try {
        if (firebaseUser) {
          const userDoc = await getDoc(doc(db, 'Users', firebaseUser.uid));
          if (userDoc.exists()) {
            const userData = userDoc.data();
            setUser({
              uid: firebaseUser.uid,
              email: firebaseUser.email,
              ...userData
            });
          }
        } else {
          setUser(null);
        }
      } catch (error) {
        console.error('Error fetching user data:', error);
        setUser(null);
      } finally {
        setLoading(false);
      }
    });

    return () => unsubscribe();
  }, []);

  return (
    <AuthContext.Provider value={{ user, loading, loginAdmin, logout }}>
      {!loading && children}
    </AuthContext.Provider>
  );
};

export const useAuth = () => useContext(AuthContext);
