import React, { Suspense, lazy } from 'react';
import { Routes, Route } from 'react-router-dom'; // Import Routes and Route
import LoginSignup from './LoginSignup'; // Import the LoginSignup component

const AdminDashboard = lazy(() => import('./admin/AdminDashboard'));
const Chat = lazy(() => import('./user/chat')); 

const App = () => {
    const [user, setUser] = useState(null);

    useEffect(() => {
        const unsubscribe = auth.onAuthStateChanged((user) => {
            setUser(user);
        });
        return () => unsubscribe();
    }, []);

    return (
        <div>
            <Suspense fallback={<div>Loading...</div>}>
                <Routes>
                    {/* Redirect to Chat if user is logged in, else show LoginSignup */}
                    <Route path="/" element={user ? <Chat /> : <LoginSignup />} />
                    <Route path="/admin" element={<AdminDashboard />} />
                    {/* Ensure user is logged in to access Chat */}
                    <Route path="/chat" element={user ? <Chat /> : <Navigate to="/login" />} />
                    <Route path="/login" element={<LoginSignup />} />
                </Routes>
            </Suspense>
        </div>
    );
};

export default App;
