import { useState, useRef, useEffect } from "react";
import { Github, Mail } from "lucide-react";
import { getDatabase, ref, get, child } from "firebase/database";
import "./App.css";
import darkblue from "./assets/darkblue.mp4";

export default function Component() {
  const [isLogin, setIsLogin] = useState(true); // Default to "Login"
  const [userId, setUserId] = useState("");
  const [password, setPassword] = useState("");
  const [confirmPassword, setConfirmPassword] = useState("");
  const [error, setError] = useState("");
  const videoRef = useRef(null);
  const containerRef = useRef(null); // Ref for scrolling

  useEffect(() => {
    if (videoRef.current) {
      videoRef.current.play().catch((error) => {
        console.error("Error attempting to play video: ", error);
      });
    }
  }, []);

  // Function to handle scroll
  const handleScroll = () => {
    if (containerRef.current) {
      containerRef.current.scrollIntoView({ behavior: "smooth" });
    }
  };

  // Function to handle form submission (login/signup)
  const handleSubmit = async (e) => {
    e.preventDefault();
    setError(""); // Clear any previous error

    const db = getDatabase();
    const userRef = ref(db, '/');

    try {
      // Check if in Sign Up mode
      if (!isLogin) {
        // Check if the user ID already exists in the database
        const snapshot = await get(child(userRef, `Admin/${userId}`));
        const facultySnapshot = await get(child(userRef, `Faculty`));
        const studentSnapshot = await get(child(userRef, `Student`));
        
        if (snapshot.exists() || facultySnapshot.val() && Object.values(facultySnapshot.val()).some(user => user.userId === userId) || studentSnapshot.val() && Object.values(studentSnapshot.val()).some(user => user.userId === userId)) {
          setError("User ID already exists");
          return;
        }

        // Continue with signup process (save password logic can be implemented as needed)
        // Here, we just alert success for the sake of this example
        alert("Sign up successful! Now you can log in.");
        return;
      }

      // Login functionality
      const adminSnapshot = await get(child(userRef, `Admin/${userId}`));
      const facultySnapshot = await get(child(userRef, `Faculty`));
      const studentSnapshot = await get(child(userRef, `Student`));

      let userRole = '';
      if (adminSnapshot.exists()) {
        userRole = 'admin';
      } else if (facultySnapshot.val() && Object.values(facultySnapshot.val()).some(user => user.userId === userId)) {
        userRole = 'faculty';
      } else if (studentSnapshot.val() && Object.values(studentSnapshot.val()).some(user => user.userId === userId)) {
        userRole = 'student';
      } else {
        setError("Invalid User ID or Password");
        return;
      }

      // Check password logic can be implemented here
      // Redirect based on role
      if (userRole === 'admin') {
        window.location.href = '/admin/AdminDashboard.jsx'; // Redirect to Admin Dashboard
      } else {
        window.location.href = '/user/chat.jsx'; // Redirect to User Chat
      }
    } catch (err) {
      setError("Network error, please try again later");
      console.error("Error connecting to backend:", err);
    }
  };

  return (
    <div className="overlay">
      <div id="gradient-overlay"></div>
      <video ref={videoRef} id="background-video" loop autoPlay muted>
        <source src={darkblue} type="video/mp4" />
        Your browser does not support the video tag.
      </video>
      <header className="header">
        <nav className="navbar">
          <ul className="nav-links">
            <li>
              <button className="nav-button">Home</button>
            </li>
            <li>
              <button className="nav-button">About</button>
            </li>
            <li>
              <button className="nav-button">Blog</button>
            </li>
            <li>
              <button className="nav-button">Contact Us</button>
            </li>
          </ul>
        </nav>
      </header>
      <div ref={containerRef} className="container">
        <div className="card">
          <div className="card-header">
            <h2>Welcome</h2>
            <p>Sign in to your account or create a new one</p>
          </div>
          {/* Authentication Options */}
          <div className="auth-options">
            <button className="auth-button">
              <Mail className="icon" />
              Google
            </button>
            <button className="auth-button">
              <Github className="icon" />
              Github
            </button>
          </div>
          {/* Divider */}
          <div className="divider">
            <span>OR</span>
          </div>
          {/* Toggle between Login and Sign Up */}
          <div className="toggle-container">
            <label>Login</label>
            <input
              type="checkbox"
              checked={!isLogin} // Reverse the checked state for "Sign Up"
              onChange={() => setIsLogin(!isLogin)}
              className="toggle-switch"
            />
            <label>Sign Up</label>
          </div>
          {/* Form Fields */}
          <form onSubmit={handleSubmit}>
            <div className="form-group">
              <label>User ID</label>
              <input
                type="text"
                placeholder="User ID"
                className="input-field"
                value={userId}
                onChange={(e) => setUserId(e.target.value)}
                required
              />
            </div>
            <div className="form-group">
              <label>Password</label>
              <input
                type="password"
                className="input-field"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                required
              />
            </div>
            {/* Conditionally show "Confirm Password" when in Sign Up mode */}
            {!isLogin && (
              <div className="form-group">
                <label>Confirm Password</label>
                <input
                  type="password"
                  className="input-field"
                  value={confirmPassword}
                  onChange={(e) => setConfirmPassword(e.target.value)}
                  required
                />
              </div>
            )}
            {/* Submit Button */}
            <button className="submit-button" type="submit">
              {isLogin ? "Login" : "Sign Up"}
            </button>
            {/* Error Display */}
            {error && <p className="error-message">{error}</p>}
          </form>
        </div>
      </div>
    </div>
  );
}
