import { useState, useRef, useEffect } from "react";
import { Github, Mail } from "lucide-react";
import "./App.css";
import darkblue from "./assets/darkblue.mp4";
import { getDatabase, ref, set, get } from "firebase/database";
import { getAuth, createUserWithEmailAndPassword } from "firebase/auth";

export default function Component() {
  const [isLogin, setIsLogin] = useState(true); // Default to "Login"
  const [userId, setUserId] = useState(""); // Changed from email to userId
  const [password, setPassword] = useState("");
  const [confirmPassword, setConfirmPassword] = useState("");
  const [error, setError] = useState("");

  const videoRef = useRef(null);
  const containerRef = useRef(null); // Ref for scrolling

  useEffect(() => {
    if (videoRef.current) {
      videoRef.current.play().catch((error) => {
        console.error("Error attempting to play video: ", error);
      });
    }
  }, []);

  // Function to handle scroll
  const handleScroll = () => {
    if (containerRef.current) {
      containerRef.current.scrollIntoView({ behavior: "smooth" });
    }
  };

  // Function to handle form submission (login/signup)
  const handleSubmit = async (e) => {
    e.preventDefault();
    setError(""); // Clear any previous error

    // Check if in Sign Up mode and passwords match
    if (!isLogin && password !== confirmPassword) {
      setError("Passwords do not match");
      return;
    }

    const auth = getAuth();
    const db = getDatabase();
    const endpoint = isLogin ? "login" : "signup"; // Decide endpoint based on mode

    if (!isLogin) {
      // Sign up logic
      try {
        // Create user with a dummy email (Firebase Auth requires email)
        const userCredential = await createUserWithEmailAndPassword(auth, userId + "@example.com", password);

        const userIdFromAuth = userCredential.user.uid; // Get user ID from Firebase Auth

        // Write user data to Realtime Database
        await set(ref(db, `users/${userIdFromAuth}`), {
          userId: userId,
          password: password, // Save password for comparison (but don't do this in production)
        });

        alert("Sign up successful!");
      } catch (err) {
        setError(err.message);
        console.error("Error signing up:", err);
      }
    } else {
      // Login logic
      const userRef = ref(db, `users/${userId}`);
      get(userRef)
        .then((snapshot) => {
          if (snapshot.exists()) {
            const userData = snapshot.val();
            // Compare stored password
            if (userData.password === password) {
              alert("Login successful!");
              // Redirect to appropriate dashboard based on role
            } else {
              setError("Invalid password");
            }
          } else {
            setError("User ID not found");
          }
        })
        .catch((err) => {
          setError("Error fetching user data: " + err.message);
        });
    }
  };

  return (
    <div className="overlay">
      <div id="gradient-overlay"></div>
      <video ref={videoRef} id="background-video" loop autoPlay muted>
        <source src={darkblue} type="video/mp4" />
        Your browser does not support the video tag.
      </video>
      <header className="header">
        <nav className="navbar">
          <ul className="nav-links">
            <li>
              <button className="nav-button">Home</button>
            </li>
            <li>
              <button className="nav-button">About</button>
            </li>
            <li>
              <button className="nav-button">Blog</button>
            </li>
            <li>
              <button className="nav-button">Contact Us</button>
            </li>
          </ul>
        </nav>
      </header>
      <div ref={containerRef} className="container">
        <div className="card">
          <div className="card-header">
            <h2>Welcome</h2>
            <p>Sign in to your account or create a new one</p>
          </div>
          {/* Authentication Options */}
          <div className="auth-options">
            <button className="auth-button">
              <Mail className="icon" />
              Google
            </button>
            <button className="auth-button">
              <Github className="icon" />
              Github
            </button>
          </div>
          {/* Divider */}
          <div className="divider">
            <span>OR</span>
          </div>
          {/* Toggle between Login and Sign Up */}
          <div className="toggle-container">
            <label>Login</label>
            <input
              type="checkbox"
              checked={!isLogin} // Reverse the checked state for "Sign Up"
              onChange={() => setIsLogin(!isLogin)}
              className="toggle-switch"
            />
            <label>Sign Up</label>
          </div>
          {/* Form Fields */}
          <form onSubmit={handleSubmit}>
            <div className="form-group">
              <label>User ID</label>
              <input
                type="text"
                placeholder="Enter your User ID"
                className="input-field"
                value={userId}
                onChange={(e) => setUserId(e.target.value)}
                required
              />
            </div>
            <div className="form-group">
              <label>Password</label>
              <input
                type="password"
                className="input-field"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                required
              />
            </div>
            {/* Conditionally show "Confirm Password" when in Sign Up mode */}
            {!isLogin && (
              <div className="form-group">
                <label>Confirm Password</label>
                <input
                  type="password"
                  className="input-field"
                  value={confirmPassword}
                  onChange={(e) => setConfirmPassword(e.target.value)}
                  required
                />
              </div>
            )}
            {/* Submit Button */}
            <button className="submit-button" type="submit">
              {isLogin ? "Login" : "Sign Up"}
            </button>
            {/* Error Display */}
            {error && <p className="error-message">{error}</p>}
          </form>
        </div>
      </div>
    </div>
  );
}
