import React, { useState, useRef, useEffect } from "react";
import { BrowserRouter as Router, Route, Routes, useNavigate } from "react-router-dom";
import { Github, Mail } from "lucide-react";
import { auth, database } from "./firebase";
import { createUserWithEmailAndPassword, signInWithEmailAndPassword } from "firebase/auth";
import { ref, get, set } from "firebase/database";
import "./App.css";
import darkblue from "./assets/darkblue.mp4";

// Import your other components
import AdminDashboard from "./admin/AdminDashboard";
import UserChat from "./user/chat";

function AuthComponent() {
  const [isLogin, setIsLogin] = useState(true);
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [confirmPassword, setConfirmPassword] = useState("");
  const [error, setError] = useState("");
  const videoRef = useRef(null);
  const containerRef = useRef(null);
  const navigate = useNavigate();

  useEffect(() => {
    if (videoRef.current) {
      videoRef.current.play().catch((error) => {
        console.error("Error attempting to play video: ", error);
      });
    }
  }, []);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError("");

    if (!isLogin && password !== confirmPassword) {
      setError("Passwords do not match");
      return;
    }

    try {
      if (isLogin) {
        // Login
        await signInWithEmailAndPassword(auth, email, password);
        const userRef = ref(database, `Users/${auth.currentUser.uid}`);
        const snapshot = await get(userRef);
        if (snapshot.exists()) {
          const userData = snapshot.val();
          if (userData.role === "Admin") {
            navigate("/admin/dashboard");
          } else {
            navigate("/user/chat");
          }
        }
      } else {
        // Sign Up
        const userRef = ref(database, `Admin/${email}`);
        const facultyRef = ref(database, `Faculty/${email}`);
        const studentRef = ref(database, `Student/${email}`);
        
        const [adminSnapshot, facultySnapshot, studentSnapshot] = await Promise.all([
          get(userRef),
          get(facultyRef),
          get(studentRef)
        ]);

        if (adminSnapshot.exists() || facultySnapshot.exists() || studentSnapshot.exists()) {
          // Email exists in the database, proceed with sign up
          const userCredential = await createUserWithEmailAndPassword(auth, email, password);
          const user = userCredential.user;
          
          // Determine the role
          let role = "Student";
          if (adminSnapshot.exists()) role = "Admin";
          else if (facultySnapshot.exists()) role = "Faculty";

          // Create a new user entry in the Users branch
          await set(ref(database, `Users/${user.uid}`), {
            email: user.email,
            role: role
          });

          // Redirect based on role
          if (role === "Admin") {
            navigate("/admin/dashboard");
          } else {
            navigate("/user/chat");
          }
        } else {
          setError("Email not found in the college database. Please contact administration.");
        }
      }
    } catch (error) {
      setError(error.message);
    }
  };

  return (
    <div className="overlay">
      <div id="gradient-overlay"></div>
      <video ref={videoRef} id="background-video" loop autoPlay muted>
        <source src={darkblue} type="video/mp4" />
        Your browser does not support the video tag.
      </video>
      <header className="header">
        <nav className="navbar">
          <ul className="nav-links">
            <li><button className="nav-button">Home</button></li>
            <li><button className="nav-button">About</button></li>
            <li><button className="nav-button">Blog</button></li>
            <li><button className="nav-button">Contact Us</button></li>
          </ul>
        </nav>
      </header>
      <div ref={containerRef} className="container">
        <div className="card">
          <div className="card-header">
            <h2>Welcome</h2>
            <p>Sign in to your account or create a new one</p>
          </div>
          <div className="auth-options">
            <button className="auth-button">
              <Mail className="icon" />
              Google
            </button>
            <button className="auth-button">
              <Github className="icon" />
              Github
            </button>
          </div>
          <div className="divider">
            <span>OR</span>
          </div>
          <div className="toggle-container">
            <label>Login</label>
            <input
              type="checkbox"
              checked={!isLogin}
              onChange={() => setIsLogin(!isLogin)}
              className="toggle-switch"
            />
            <label>Sign Up</label>
          </div>
          <form onSubmit={handleSubmit}>
            <div className="form-group">
              <label>Email</label>
              <input
                type="email"
                placeholder="m@example.com"
                className="input-field"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
                required
              />
            </div>
            <div className="form-group">
              <label>Password</label>
              <input
                type="password"
                className="input-field"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                required
              />
            </div>
            {!isLogin && (
              <div className="form-group">
                <label>Confirm Password</label>
                <input
                  type="password"
                  className="input-field"
                  value={confirmPassword}
                  onChange={(e) => setConfirmPassword(e.target.value)}
                  required
                />
              </div>
            )}
            <button className="submit-button" type="submit">
              {isLogin ? "Login" : "Sign Up"}
            </button>
            {error && <p className="error-message">{error}</p>}
          </form>
        </div>
      </div>
    </div>
  );
}

export default function App() {
  return (
    <Router>
      <Routes>
        <Route path="/" element={<AuthComponent />} />
        <Route path="/admin/dashboard" element={<AdminDashboard />} />
        <Route path="/user/chat" element={<UserChat />} />
      </Routes>
    </Router>
  );
}
