import React, { useState, useRef } from "react";
import axios from "axios";
import {
  Upload,
  Bell,
  BarChart2,
  MessageSquare,
  MoreVertical,
  FileText,
  FileImage,
  FileSpreadsheet,
  Send,
} from "lucide-react";
import "./AdminDashboard.css";

export default function AdminDashboard() {
  const [activeTab, setActiveTab] = useState("Upload");
  const [announcement, setAnnouncement] = useState("");
  const [files, setFiles] = useState([]);
  const [uploading, setUploading] = useState(false);
  const [uploadError, setUploadError] = useState(null);
  const [userType, setUserType] = useState("Students"); // Dropdown state
  const fileInputRef = useRef(null);

  const tabs = [
    { name: "Upload", icon: Upload },
    { name: "Announcements", icon: Bell },
    { name: "Stats", icon: BarChart2 },
    { name: "Chatbot", icon: MessageSquare },
  ];

  const handleAnnouncementSubmit = (e) => {
    e.preventDefault();
    console.log("Announcement submitted:", announcement);
    console.log("Uploaded files:", files);
    setAnnouncement("");
    setFiles([]);
  };

  const handleFileUpload = async (e) => {
    const selectedFiles = Array.from(e.target.files);
    setUploading(true);
    setUploadError(null);
    const formData = new FormData();
    selectedFiles.forEach((file) => {
      formData.append("files", file);
    });

    try {
      const response = await axios.post(
        "http://localhost:8000/upload/",
        formData,
        {
          headers: {
            "Content-Type": "multipart/form-data",
          },
        }
      );
      console.log(response.data);
      const newFiles = selectedFiles.map((file) => ({
        name: file.name,
        size: (file.size / 1024 / 1024).toFixed(2) + " MB",
        uploaded: new Date().toLocaleDateString(),
        icon: getFileIcon(file.name),
      }));
      setFiles((prevFiles) => [...newFiles, ...prevFiles]);
    } catch (error) {
      console.error("Error uploading file:", error);
      setUploadError("Failed to upload files. Please try again.");
    } finally {
      setUploading(false);
    }
  };

  const triggerFileUpload = () => {
    fileInputRef.current.click();
  };

  const getFileIcon = (fileName) => {
    if (fileName.endsWith(".pdf")) return FileText;
    if (fileName.endsWith(".pptx")) return FileImage;
    if (fileName.endsWith(".xlsx")) return FileSpreadsheet;
    return FileText;
  };

  return (
    <div className="app-container">
      <nav>
        <h1>Nexus Control</h1>
        <ul>
          {tabs.map((tab) => (
            <li key={tab.name}>
              <button
                className={activeTab === tab.name ? "active" : ""}
                onClick={() => setActiveTab(tab.name)}
              >
                <tab.icon />
                {tab.name}
              </button>
            </li>
          ))}
        </ul>
      </nav>
      <main className="main-layout">
        <div className="upload-section">
          {activeTab === "Upload" && (
            <>
              <h2>Data Upload Interface</h2>
              <div className="card-1 card">
                <div className="upload-area">
                  <p>Initiate data transfer or select files for upload.</p>
                  <button onClick={triggerFileUpload} disabled={uploading}>
                    {uploading ? "Uploading..." : "Upload Files"}
                  </button>
                  <input
                    type="file"
                    ref={fileInputRef}
                    className="hidden"
                    onChange={handleFileUpload}
                    multiple
                  />
                </div>
                {uploadError && (
                  <p className="error-message">{uploadError}</p>
                )}
              </div>
              <div className="card">
                <table>
                  <thead>
                    <tr>
                      <th>File</th>
                      <th>Size</th>
                      <th>Uploaded</th>
                      <th>Actions</th>
                    </tr>
                  </thead>
                  <tbody>
                    {files.map((file, index) => (
                      <tr key={index}>
                        <td>
                          <div className="file-info">
                            <div className="file-icon">
                              {file.icon && <file.icon />}
                            </div>
                            <span>{file.name}</span>
                          </div>
                        </td>
                        <td>{file.size}</td>
                        <td>{file.uploaded}</td>
                        <td>
                          <button className="action-button">
                            <MoreVertical />
                          </button>
                        </td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              </div>
            </>
          )}
        </div>
        <div className="right-panel">
          {/* Dropdown for user type */}
          <div className="dropdown-container">
            <label htmlFor="userType">Select User Type:</label>
            <select
              id="userType"
              value={userType}
              onChange={(e) => setUserType(e.target.value)}
            >
              <option value="Students">Students</option>
              <option value="Teachers">Teachers</option>
              <option value="HOD/Dean">HOD/Dean</option>
            </select>
          </div>

          {/* Announcement section */}
          {activeTab === "Announcements" && (
            <>
              <h2>Broadcast Announcements</h2>
              <div className="card">
                <form onSubmit={handleAnnouncementSubmit}>
                  <textarea
                    value={announcement}
                    onChange={(e) => setAnnouncement(e.target.value)}
                  />
                  <button type="submit">Submit Announcement</button>
                </form>
              </div>
            </>
          )}
        </div>
      </main>
    </div>
  );
}
