import React, { useState, useRef, useEffect } from 'react';  
import axios from 'axios';  
import { auth, db, storage } from '../firebase';  
import { doc, getDoc } from 'firebase/firestore';  
import { UserCircle, MessageSquare, PlusCircle, Bell, Send, X, RefreshCw, Volume2 } from 'lucide-react';  
import './chat.css';  
import darkblue from "../assets/darkblue.mp4";  
import { useNavigate } from 'react-router-dom';  
  
const ChatbotUI = () => {  
  const navigate = useNavigate();  
  const [message, setMessage] = useState('');  
  const [messages, setMessages] = useState([  
   { text: 'Hello! How can I assist you today?', type: 'ai' }  
  ]);  
  const [showAnnouncements, setShowAnnouncements] = useState(false);  
  const [showUserPanel, setShowUserPanel] = useState(false);  
  const [isSending, setIsSending] = useState(false);  
  const [userProfile, setUserProfile] = useState({  
   name: '',  
   email: '',  
   role: '',  
   photo: ''  
  });  
  
  const videoRef = useRef(null);  
  const chatContainerRef = useRef(null);  
  
  const announcements = [  
   "New feature: Role-based responses now available!",  
   "Maintenance scheduled for tonight at 2 AM UTC",  
   "Check out our new dark mode in settings!"  
  ];  
  
  // Fetch user profile data on component mount  
  useEffect(() => {  
   const fetchUserProfile = async () => {  
    const user = auth.currentUser;  
    if (user) {  
      try {  
       const userDoc = await getDoc(doc(db, 'Users', user.uid));  
       if (userDoc.exists()) {  
        const userData = userDoc.data();  
        setUserProfile({  
          name: userData.name,  
          email: user.email,  
          role: userData.role,  
          photo: userData.photo || ''  
        });  
       }  
      } catch (error) {  
       console.error('Error fetching user profile:', error);  
      }  
    } else {  
      // Redirect to login if no user is found  
      navigate('/login');  
    }  
   };  
  
   fetchUserProfile();  
  }, [navigate]);  
  
  // Scroll to the bottom when new messages arrive  
  useEffect(() => {  
   if (chatContainerRef.current) {  
    chatContainerRef.current.scrollTop = chatContainerRef.current.scrollHeight;  
   }  
  }, [messages]);  
  
  // Function to send a message  
  const sendMessage = async () => {  
   if (message.trim() && !isSending) {  
    const userMessage = { text: message, type: 'user' };  
    setMessages(prevMessages => [...prevMessages, userMessage]);  
    setMessage('');  
    setIsSending(true);  
  
    try {  
      const response = await axios.post('http://localhost:8000/chat/', {  
       message,  
       userRole: userProfile.role  
      });  
      const botMessage = { text: response.data.response, type: 'ai' };  
      setMessages(prevMessages => [...prevMessages, botMessage]);  
    } catch (error) {  
      console.error('Error sending message:', error);  
      const errorMessage = { text: 'Sorry, I encountered an error. Please try again.', type: 'ai' };  
      setMessages(prevMessages => [...prevMessages, errorMessage]);  
    } finally {  
      setIsSending(false);  
    }  
   }  
  };  
  
  // Function to regenerate a response  
  const regenerateResponse = async (index) => {  
   setIsSending(true);  
   try {  
    const response = await axios.post('http://localhost:8000/regenerate/', {  
      message: messages[index].text,  
      userRole: userProfile.role  
    });  
    setMessages(prevMessages => {  
      const newMessages = [...prevMessages];  
      newMessages[index] = { ...newMessages[index], text: response.data.response };  
      return newMessages;  
    });  
   } catch (error) {  
    console.error('Error regenerating response:', error);  
   } finally {  
    setIsSending(false);  
   }  
  };  
  
  // Function to play audio for text  
  const playAudio = (text) => {  
   const utterance = new SpeechSynthesisUtterance(text);  
   speechSynthesis.speak(utterance);  
  };  
  
  // Logout function  
  const handleLogout = async () => {  
   try {  
      await auth.signOut(); // Sign out the user  
      setUserProfile({ name: '', email: '', role: '', photo: '' }); // Reset user profile state  
      console.log('User logged out successfully');  
      navigate('/login'); // Navigate to the LoginSignup page  
   } catch (error) {  
      console.error('Error logging out:', error);  
   }  
};  
  
  // Function to handle doc: command  
  const handleDocCommand = async (fileName) => {  
   try {  
    const filesRef = storage.ref('files');  
    const files = await filesRef.listAll();  
    const matchingFiles = files.items.filter(file => file.name.toLowerCase().includes(fileName.toLowerCase()));  
    if (matchingFiles.length > 0) {  
      const fileRef = matchingFiles[0];  
      const fileMetadata = await fileRef.getMetadata();  
      if (fileMetadata.customMetadata.role === userProfile.role) {  
       const fileUrl = await fileRef.getDownloadURL();  
       const fileMessage = { text: `File: ${fileRef.name} - ${fileMetadata.customMetadata.role}`, type: 'ai' };  
       setMessages(prevMessages => [...prevMessages, fileMessage]);  
      } else {  
       const errorMessage = { text: 'You do not have permission to access this file.', type: 'ai' };  
       setMessages(prevMessages => [...prevMessages, errorMessage]);  
      }  
    } else {  
      const errorMessage = { text: 'File not found.', type: 'ai' };  
      setMessages(prevMessages => [...prevMessages, errorMessage]);  
    }  
   } catch (error) {  
    console.error('Error handling doc: command:', error);  
   }  
  };  
  
  return (  
   <div className="chatbot-container">  
    <video ref={videoRef} id="background-video" loop autoPlay muted>  
      <source src={darkblue} type="video/mp4" />  
      Your browser does not support the video tag.  
    </video>  
      
    <div className="chatbot-window">  
      <div className="sidebar">  
       <button className="sidebar-button" onClick={() => setShowUserPanel(!showUserPanel)}>  
        <UserCircle />  
       </button>  
       <button className="sidebar-button">  
        <MessageSquare />  
       </button>  
       <button className="sidebar-button">  
        <PlusCircle />  
       </button>  
       <button className="sidebar-button" onClick={() => setShowAnnouncements(!showAnnouncements)}>  
        <Bell />  
       </button>  
      </div>  
  
      <div className="main-content" style={{ marginRight: (showAnnouncements || showUserPanel) ? '320px' : '0' }}>  
       <div className="chat-area" ref={chatContainerRef}>  
        {messages.map((msg, index) => (  
          <div key={index} className={`message-container ${msg.type === 'user' ? 'user-message-container' : 'ai-message-container'}`}>  
           <div className={`message ${msg.type === 'user' ? 'user-message' : 'ai-message'}`}>  
            {msg.text}  
           </div>  
           {msg.type === 'ai' && (  
            <div className="message-actions">  
              <button className="action-button" onClick={() => regenerateResponse(index)} disabled={isSending}>  
               <RefreshCw size={16} />  
              </button>  
              <button className="action-button" onClick={() => playAudio(msg.text)}>  
               <Volume2 size={16} />  
              </button>  
            </div>  
           )}  
          </div>  
        ))}  
       </div>  
        
       <div className="input-area">  
        <input  
          type="text"  
          placeholder="Type a message..."  
          value={message}  
          onChange={(e) => setMessage(e.target.value)}  
          onKeyDown={(e) => {  
           if (e.key === 'Enter') {  
            if (message.startsWith('doc:')) {  
              const fileName = message.substring(4).trim();  
              handleDocCommand(fileName);  
            } else {  
              sendMessage();  
            }  
           }  
          }}  
          disabled={isSending}  
        />  
        <button className="send-button" onClick={sendMessage} disabled={isSending}>  
          {isSending ? <RefreshCw size={20} className="spin" /> : <Send size={20} />}  
        </button>  
       </div>  
      </div>  
  
      {showAnnouncements && (  
       <div className="announcements-panel">  
        <div className="panel-header">  
          <h2>Announcements</h2>  
          <button onClick={() => setShowAnnouncements(false)}>  
           <X size={20} />  
          </button>  
        </div>  
        <div className="announcements-list">  
          {announcements.map((announcement, index) => (  
           <div key={index} className="announcement">  
            {announcement}  
           </div>  
          ))}  
        </div>  
       </div>  
      )}  
  
      {showUserPanel && (  
       <div className="user-panel">  
        <div className="panel-header">  
          <h2>Profile</h2>  
          <button onClick={() => setShowUserPanel(false)}>  
           <X size={20} />  
          </button>  
        </div>  
        <div className="profile-content">  
          <div className="profile-avatar">  
           {userProfile.photo ? (  
            <img src={userProfile.photo} alt="User Avatar" />  
           ) : (  
            <UserCircle size={64} />  
           )}  
          </div>  
          <div className="profile-info">  
           <h3>{userProfile.name}</h3>  
           <p>{userProfile.email}</p>  
           <p className="role-badge">{userProfile.role}</p>  
          </div>  
          <button className="logout-button" onClick={handleLogout}>  
          Logout  
          </button>  
        </div>  
       </div>  
      )}  
    </div>  
   </div>  
  );  
};  
  
export default ChatbotUI;
