import { useState, useRef, useEffect } from "react";
import { Github, Mail } from "lucide-react";
import "./App.css";
import darkblue from "./assets/darkblue.mp4";

export default function Component() {
  const [isLogin, setIsLogin] = useState(true); // Default to "Login"
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [confirmPassword, setConfirmPassword] = useState("");
  const [name, setName] = useState(""); // Name field
  const [role, setRole] = useState(""); // Role selection
  const [className, setClassName] = useState(""); // Class for students
  const [year, setYear] = useState(""); // Year for students
  const [designation, setDesignation] = useState(""); // Designation for faculty
  const [error, setError] = useState("");

  const videoRef = useRef(null);

  useEffect(() => {
    if (videoRef.current) {
      videoRef.current.play().catch((error) => {
        console.error("Error attempting to play video: ", error);
      });
    }
  }, []);

  // Function to handle form submission (login/signup)
  const handleSubmit = async (e) => {
    e.preventDefault();
    setError(""); // Clear any previous error

    // Check if in Sign Up mode and passwords match
    if (!isLogin && password !== confirmPassword) {
      setError("Passwords do not match");
      return;
    }

    const endpoint = isLogin ? "/login" : "/signup"; // Decide endpoint based on mode

    try {
      const response = await fetch(`http://localhost:5000/api${endpoint}`, {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({
          email,
          password,
          ...(isLogin ? {} : { name, role, className, year, designation }), // Additional fields on Sign Up
        }),
      });

      const data = await response.json();

      if (response.ok) {
        // Handle success (e.g., redirect, store token, etc.)
        alert("Authentication successful");
      } else {
        // Handle error
        setError(data.message || "Something went wrong");
      }
    } catch (err) {
      setError("Network error, please try again later");
      console.error("Error connecting to backend:", err);
    }
  };

  return (
    <div className="overlay">
      <div id="gradient-overlay"></div>
      <video ref={videoRef} id="background-video" loop autoPlay muted>
        <source src={darkblue} type="video/mp4" />
        Your browser does not support the video tag.
      </video>
      <header className="header">
        <nav className="navbar">
          <ul className="nav-links">
            <li>
              <button className="nav-button">Home</button>
            </li>
            <li>
              <button className="nav-button">About</button>
            </li>
            <li>
              <button className="nav-button">Blog</button>
            </li>
            <li>
              <button className="nav-button">Contact Us</button>
            </li>
          </ul>
        </nav>
      </header>
      <div className="container">
        <div className="card">
          <div className="card-header">
            <h2>Welcome</h2>
            <p>Sign in to your account or create a new one</p>
          </div>
          {/* Authentication Options */}
          <div className="auth-options">
            <button className="auth-button">
              <Mail className="icon" />
              Google
            </button>
            <button className="auth-button">
              <Github className="icon" />
              Github
            </button>
          </div>
          {/* Divider */}
          <div className="divider">
            <span>OR</span>
          </div>
          {/* Toggle between Login and Sign Up */}
          <div className="toggle-container">
            <label>Login</label>
            <input
              type="checkbox"
              checked={!isLogin} // Reverse the checked state for "Sign Up"
              onChange={() => setIsLogin(!isLogin)}
              className="toggle-switch"
            />
            <label>Sign Up</label>
          </div>
          {/* Form Fields */}
          <form onSubmit={handleSubmit}>
            {!isLogin && (
              <>
                <div className="form-group">
                  <label>Name</label>
                  <input
                    type="text"
                    placeholder="Enter your name"
                    className="input-field"
                    value={name}
                    onChange={(e) => setName(e.target.value)}
                    required={!isLogin}
                  />
                </div>

                {/* Role Selection */}
                <div className="form-group">
                  <label>Role</label>
                  <select
                    className="input-field"
                    value={role}
                    onChange={(e) => setRole(e.target.value)}
                    required
                  >
                    <option value="">Select Role</option>
                    <option value="student">Student</option>
                    <option value="faculty">Faculty</option>
                  </select>
                </div>
              </>
            )}
            <div className="form-group">
              <label>Email</label>
              <input
                type="email"
                placeholder="m@example.com"
                className="input-field"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
                required
              />
            </div>
            <div className="form-group">
              <label>Password</label>
              <input
                type="password"
                className="input-field"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                required
              />
            </div>
            {!isLogin && (
              <div className="form-group">
                <label>Confirm Password</label>
                <input
                  type="password"
                  className="input-field"
                  value={confirmPassword}
                  onChange={(e) => setConfirmPassword(e.target.value)}
                  required
                />
              </div>
            )}

            {/* Conditional Fields based on Role */}
            {!isLogin && role === "student" && (
              <>
                <div className="form-group">
                  <label>Class</label>
                  <select
                    className="input-field"
                    value={className}
                    onChange={(e) => setClassName(e.target.value)}
                    required
                  >
                    <option value="">Select Class</option>
                    <option value="BTech CE">BTech CE</option>
                    <option value="CSBS">CSBS</option>
                    <option value="AI&DS">AI&DS</option>
                    <option value="MBA Tech">MBA Tech</option>
                  </select>
                </div>
                <div className="form-group">
                  <label>Year</label>
                  <select
                    className="input-field"
                    value={year}
                    onChange={(e) => setYear(e.target.value)}
                    required
                  >
                    <option value="">Select Year</option>
                    <option value="1st">1st</option>
                    <option value="2nd">2nd</option>
                    <option value="3rd">3rd</option>
                    <option value="4th">4th</option>
                  </select>
                </div>
              </>
            )}

            {!isLogin && role === "faculty" && (
              <div className="form-group">
                <label>Designation</label>
                <select
                  className="input-field"
                  value={designation}
                  onChange={(e) => setDesignation(e.target.value)}
                  required
                >
                  <option value="">Select Designation</option>
                  <option value="Assistant Professor">Assistant Professor</option>
                  <option value="HOD/Dean">HOD/Dean</option>
                </select>
              </div>
            )}

            {/* Submit Button */}
            <button className="submit-button" type="submit">
              {isLogin ? "Login" : "Sign Up"}
            </button>
            {/* Error Display */}
            {error && <p className="error-message">{error}</p>}
          </form>
        </div>
      </div>
    </div>
  );
}
