import React, { useState, useRef } from "react";
import axios from "axios";
import {
  Upload,
  Bell,
  BarChart2,
  MessageSquare,
  MoreVertical,
  FileText,
  FileImage,
  FileSpreadsheet,
  File,
  FileType,
  Image,
} from "lucide-react";
import "./AdminDashboard.css";

export default function AdminDashboard() {
  const [activeTab, setActiveTab] = useState("Upload");
  const [announcement, setAnnouncement] = useState("");
  const [files, setFiles] = useState([]);
  const [uploading, setUploading] = useState(false);
  const [uploadError, setUploadError] = useState(null);
  const [userType, setUserType] = useState("Students");
  const [selectedRole, setSelectedRole] = useState("Students");
  const fileInputRef = useRef(null);

  const tabs = [
    { name: "Upload", icon: Upload },
    { name: "Announcements", icon: Bell },
    { name: "Stats", icon: BarChart2 },
    { name: "Chatbot", icon: MessageSquare },
  ];

  const handleAnnouncementSubmit = (e) => {
    e.preventDefault();
    console.log("Announcement submitted:", announcement);
    console.log("Uploaded files:", files);
    setAnnouncement("");
    setFiles([]);
  };

  const handleFileUpload = async (e) => {
    const selectedFiles = Array.from(e.target.files);
    setUploading(true);
    setUploadError(null);
    const formData = new FormData();
    selectedFiles.forEach((file) => {
      formData.append("files", file);
    });
    formData.append("role", selectedRole.toLowerCase());

    try {
      const response = await axios.post(
        "http://localhost:8000/upload/",
        formData,
        {
          headers: {
            "Content-Type": "multipart/form-data",
          },
        }
      );
      console.log(response.data);
      const newFiles = selectedFiles.map((file) => ({
        name: file.name,
        size: (file.size / 1024 / 1024).toFixed(2) + " MB",
        uploaded: new Date().toLocaleDateString(),
        icon: getFileIcon(file.name),
        role: selectedRole,
      }));
      setFiles((prevFiles) => [...newFiles, ...prevFiles]);
    } catch (error) {
      console.error("Error uploading file:", error);
      setUploadError("Failed to upload files. Please try again.");
    } finally {
      setUploading(false);
    }
  };

  const triggerFileUpload = () => {
    fileInputRef.current.click();
  };

  const getFileIcon = (fileName) => {
    const ext = fileName.split('.').pop().toLowerCase();
    switch (ext) {
      case 'pdf':
        return FileText;
      case 'csv':
      case 'xlsx':
        return FileSpreadsheet;
      case 'txt':
        return FileText;
      case 'docx':
        return FileType;
      case 'jpeg':
      case 'jpg':
      case 'png':
        return Image;
      default:
        return File;
    }
  };

  return (
    <div className="app-container">
      <nav>
        <h1>Nexus Control</h1>
        <ul>
          {tabs.map((tab) => (
            <li key={tab.name}>
              <button
                className={activeTab === tab.name ? "active" : ""}
                onClick={() => setActiveTab(tab.name)}
              >
                <tab.icon />
                {tab.name}
              </button>
            </li>
          ))}
        </ul>
      </nav>
      <main className="main-layout">
        {(activeTab === "Upload" || activeTab === "Announcements") && (
          <div className="dropdown-container">
            <select
              id="userType"
              value={selectedRole}
              onChange={(e) => setSelectedRole(e.target.value)}
              className="user-type-dropdown"
            >
              <option value="Students">Students</option>
              <option value="Teachers">Teachers</option>
              <option value="HOD/Dean">HOD/Dean</option>
            </select>
          </div>
        )}
        
        <div className="content-area">
          {activeTab === "Upload" && (
            <div className="upload-section">
              <h2>Data Upload Interface</h2>
              <div className="card-1 card">
                <div className="upload-area">
                  <p>Initiate data transfer or select files for upload.</p>
                  <p className="file-types">Supported file types: PDF, CSV, TXT, DOCX, XLSX, JPEG, JPG, PNG</p>
                  <button onClick={triggerFileUpload} disabled={uploading}>
                    {uploading ? "Uploading..." : "Upload Files"}
                  </button>
                  <input
                    type="file"
                    ref={fileInputRef}
                    className="hidden"
                    onChange={handleFileUpload}
                    multiple
                    accept=".pdf,.csv,.txt,.docx,.xlsx,.jpeg,.jpg,.png"
                  />
                </div>
                {uploadError && (
                  <p className="error-message">{uploadError}</p>
                )}
              </div>
              <div className="card">
                <table>
                  <thead>
                    <tr>
                      <th>File</th>
                      <th>Size</th>
                      <th>Uploaded</th>
                      <th>Role</th>
                      <th>Actions</th>
                    </tr>
                  </thead>
                  <tbody>
                    {files.map((file, index) => (
                      <tr key={index}>
                        <td>
                          <div className="file-info">
                            <div className="file-icon">
                              {file.icon && <file.icon />}
                            </div>
                            <span>{file.name}</span>
                          </div>
                        </td>
                        <td>{file.size}</td>
                        <td>{file.uploaded}</td>
                        <td>{file.role}</td>
                        <td>
                          <button className="action-button">
                            <MoreVertical />
                          </button>
                        </td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              </div>
            </div>
          )}
          
          {activeTab === "Announcements" && (
            <div className="announcements-section">
              <h2>Broadcast Announcements</h2>
              <div className="card">
                <form onSubmit={handleAnnouncementSubmit}>
                  <textarea
                    value={announcement}
                    onChange={(e) => setAnnouncement(e.target.value)}
                    placeholder="Type your announcement here..."
                  />
                  <button type="submit">Submit Announcement</button>
                </form>
              </div>
            </div>
          )}
          
          {activeTab === "Stats" && (
            <div className="stats-section">
              <h2>Statistics</h2>
              <div className="card">
                <p>Statistics content goes here.</p>
              </div>
            </div>
          )}
          
          {activeTab === "Chatbot" && (
            <div className="chatbot-section">
              <h2>Chatbot Interface</h2>
              <div className="card">
                <p>Chatbot interface goes here.</p>
              </div>
            </div>
          )}
        </div>
      </main>
    </div>
  );
}
