import { useState, useRef, useEffect } from "react";
import { Github, Mail } from "lucide-react";
import { auth, database } from "./firebase"; // Import Firebase auth and database
import {
  signInWithEmailAndPassword,
  createUserWithEmailAndPassword
} from "firebase/auth";
import { ref, get, set } from "firebase/database"; // Import necessary functions from Firebase Database
import "./App.css";
import darkblue from "./assets/darkblue.mp4";

export default function Component() {
  const [isLogin, setIsLogin] = useState(true); // Default to "Login"
  const [userId, setUserId] = useState(""); // Change from email to userId
  const [password, setPassword] = useState("");
  const [confirmPassword, setConfirmPassword] = useState("");
  const [error, setError] = useState("");

  const videoRef = useRef(null);
  const containerRef = useRef(null); // Ref for scrolling

  useEffect(() => {
    if (videoRef.current) {
      videoRef.current.play().catch((error) => {
        console.error("Error attempting to play video: ", error);
      });
    }
  }, []);

  // Function to handle scroll
  const handleScroll = () => {
    if (containerRef.current) {
      containerRef.current.scrollIntoView({ behavior: "smooth" });
    }
  };

  // Function to handle form submission (login/signup)
  const handleSubmit = async (e) => {
    e.preventDefault();
    setError(""); // Clear any previous error

    const userRef = ref(database, `Student/${userId}`); // Path to Student data

    try {
      if (!isLogin) {
        // Check if user ID exists for Sign Up
        const snapshot = await get(userRef);
        if (snapshot.exists()) {
          setError("User ID already exists. Please use a different ID.");
          return;
        }
        // If the user ID doesn't exist, create the user
        await createUserWithEmailAndPassword(auth, userId, password);
        // Save user info in the database (can modify as needed)
        await set(userRef, {
          Name: "New User", // You might want to gather more info from the user
          userId,
          // Additional fields can be added as needed
        });
        alert("User created successfully");
      } else {
        // For login, check credentials
        await signInWithEmailAndPassword(auth, userId, password);
        // Redirect user based on their role
        const userSnapshot = await get(userRef);
        if (userSnapshot.exists()) {
          const userData = userSnapshot.val();
          if (userData.role === "Admin") {
            // Redirect to Admin Dashboard
            window.location.href = "/admin/AdminDashboard"; // Adjust path as necessary
          } else {
            // Redirect to User Chat
            window.location.href = "/user/chat"; // Adjust path as necessary
          }
        } else {
          setError("User not found");
        }
      }
    } catch (err) {
      setError(err.message || "Something went wrong");
      console.error("Error with Firebase authentication:", err);
    }
  };

  return (
    <div className="overlay">
      <div id="gradient-overlay"></div>
      <video ref={videoRef} id="background-video" loop autoPlay muted>
        <source src={darkblue} type="video/mp4" />
        Your browser does not support the video tag.
      </video>
      <header className="header">
        <nav className="navbar">
          <ul className="nav-links">
            <li>
              <button className="nav-button">Home</button>
            </li>
            <li>
              <button className="nav-button">About</button>
            </li>
            <li>
              <button className="nav-button">Blog</button>
            </li>
            <li>
              <button className="nav-button">Contact Us</button>
            </li>
          </ul>
        </nav>
      </header>
      <div ref={containerRef} className="container">
        <div className="card">
          <div className="card-header">
            <h2>Welcome</h2>
            <p>Sign in to your account or create a new one</p>
          </div>
          {/* Authentication Options */}
          <div className="auth-options">
            <button className="auth-button">
              <Mail className="icon" />
              Google
            </button>
            <button className="auth-button">
              <Github className="icon" />
              Github
            </button>
          </div>
          {/* Divider */}
          <div className="divider">
            <span>OR</span>
          </div>
          {/* Toggle between Login and Sign Up */}
          <div className="toggle-container">
            <label>Login</label>
            <input
              type="checkbox"
              checked={!isLogin} // Reverse the checked state for "Sign Up"
              onChange={() => setIsLogin(!isLogin)}
              className="toggle-switch"
            />
            <label>Sign Up</label>
          </div>
          {/* Form Fields */}
          <form onSubmit={handleSubmit}>
            <div className="form-group">
              <label>User ID</label>
              <input
                type="text"
                placeholder="Enter your User ID"
                className="input-field"
                value={userId}
                onChange={(e) => setUserId(e.target.value)}
                required
              />
            </div>
            <div className="form-group">
              <label>Password</label>
              <input
                type="password"
                className="input-field"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                required
              />
            </div>
            {/* Conditionally show "Confirm Password" when in Sign Up mode */}
            {!isLogin && (
              <div className="form-group">
                <label>Confirm Password</label>
                <input
                  type="password"
                  className="input-field"
                  value={confirmPassword}
                  onChange={(e) => setConfirmPassword(e.target.value)}
                  required
                />
              </div>
            )}
            {/* Submit Button */}
            <button className="submit-button" type="submit">
              {isLogin ? "Login" : "Sign Up"}
            </button>
            {/* Error Display */}
            {error && <p className="error-message">{error}</p>}
          </form>
        </div>
      </div>
    </div>
  );
}
